mapper.insert:
INSERT INTO SHOP_MEMBER(
    MEMBER_ID
    , MEMBER_PW
    , MEMBER_NAME
    , GENDER
    , MEMBER_EMAIL
    , MEMBER_TEL
    , MEMBER_ADDR
    , ADDR_DETAIL
    , POST_CODE
    ) VALUES(
      #{memberId}
    , #{memberPw}
    , #{memberName}
    , #{gender}
    , #{memberEmail}
    , #{memberTel}
    , #{memberAddr}
    , #{addrDetail}
    , #{postCode}

    )

mapper.select:
SELECT MEMBER_ID
    ,MEMBER_ROLL
    ,MEMBER_NAME
FROM SHOP_MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PW = #{memberPw}
